                                'opacity': '1',
                                'visibility': 'visible'
                            });
                        }

                    } else {
                        $(this).css({
                            'transform': 'translateY(100px)',
                            'opacity': '0',
                            'visibility': 'hidden'
                        });
                    }
                });
                $('.contact-show-hide').each(function(i) {
                    let objectHeight = $(this).offset().top + $(this).outerHeight();


                    if (windowHeight > objectHeight) {
                        if ($(this).css('opacity') == 0) {
                            $(this).css({
                                'transform': 'translateY(0)',
                                'opacity': '1',
                                'visibility': 'visible'
                            });
                        }

                    } else {
                        $(this).css({
                            'transform': 'translateY(100px)',
                            'opacity': '0',
                            'visibility': 'hidden'
                        });
                    }
                });

                $('.progress').each(function(i) {
                    let objectHeight = $(this).offset().top + $(this).outerHeight();

                    let digit1 = $('.digit1').text();
                    let digit2 = $('.digit2').text();
                    let digit3 = $('.digit3').text();
                    let digit4 = $('.digit4').text();
                    let digit5 = $('.digit5').text();
                    let digit6 = $('.digit6').text();
                    let digit7 = $('.digit7').text();
                    let digit8 = $('.digit8').text();
                    let digit9 = $('.digit9').text();
                    let digit10 = $('.digit10').text();
                    if (windowHeight > objectHeight) {

                        $('.progres1').css('width', digit1);
                        $('.progres2').css('width', digit2);
                        $('.progres3').css('width', digit3);
                        $('.progres4').css('width', digit4);
                        $('.progres5').css('width', digit5);
                        $('.progres6').css('width', digit6);
                        $('.progres7').css('width', digit7);
                        $('.progres8').css('width', digit8);
                        $('.progres9').css('width', digit9);
                        $('.progres10').css('width', digit10);
                    } else {
                        $('.progress').css('width', '0');
                        
                    }
                });
            });



            // Cache selectors
            var lastId,
                topMenu = $(".main-menu"),
                topMenuHeight = topMenu.outerHeight() + 1,
                // All list items
                menuItems = topMenu.find("a"),
                // Anchors corresponding to menu items
                scrollItems = menuItems.map(function() {
                    var item = $($(this).attr("href"));
                    if (item.length) {
                        return item;
                    }
                });

            // Bind click handler to menu items
            // so we can get a fancy scroll animation
            menuItems.click(function(e) {
                var href = $(this).attr("href"),
                    offsetTop = href === "#" ? 0 : $(href).offset().top - topMenuHeight + 1;
                $('html, body').stop().animate({
                    scrollTop: offsetTop
                }, 850);
                e.preventDefault();
            });

            // Bind to scroll
            $(window).scroll(function() {
                // Get container scroll position
                var fromTop = $(this).scrollTop() + topMenuHeight;

                // Get id of current scroll item
                var cur = scrollItems.map(function() {
                    if ($(this).offset().top < fromTop)
                        return this;
                });
                // Get the id of the current element
                cur = cur[cur.length - 1];
                var id = cur && cur.length ? cur[0].id : "";

                if (lastId !== id) {
                    lastId = id;
                    // Set/remove active class
                    menuItems
                        .parent().removeClass("active")
                        .end().filter("[href=#" + id + "]").parent().addClass("active");
                }
            });


        });
    </script>
</body>

</html>